import "jsr:@supabase/functions-js/edge-runtime.d.ts";
import { createClient } from "npm:@supabase/supabase-js@2";

type CodeCompletionRequest = {
  code: string;
  language: string;
  cursorPosition?: number;
  model?: string;
  temperature?: number;
  maxTokens?: number;
};

type CodeCompletionResponse = {
  completion: string;
  model: string;
  duration_ms: number;
  tokens?: { prompt?: number; completion?: number; total?: number };
};

const SUPABASE_URL = Deno.env.get("SUPABASE_URL")!;
const SERVICE_ROLE = Deno.env.get("SUPABASE_SERVICE_ROLE_KEY")!;
const OPENROUTER_API_KEY = Deno.env.get("OPENROUTER_API_KEY")!;
const OPENROUTER_BASE_URL = "https://openrouter.ai/api/v1";

// Default model - good for code completion
const DEFAULT_MODEL = "deepseek/deepseek-coder";

const supabase = createClient(SUPABASE_URL, SERVICE_ROLE, { auth: { persistSession: false } });

Deno.serve(async (req) => {
  try {
    if (req.method !== "POST") {
      return new Response("Method Not Allowed", { status: 405 });
    }

    // Auth
    const auth = req.headers.get("Authorization") || "";
    const { data: user } = await supabase.auth.getUser(auth.replace("Bearer ", ""));
    if (!user?.user) {
      return new Response(JSON.stringify({ error: "Unauthorized" }), { status: 401 });
    }

    // Parse request
    const body = (await req.json()) as CodeCompletionRequest;
    const code = (body.code ?? "").trim();
    const language = body.language || "javascript";
    const model = body.model || DEFAULT_MODEL;
    const temperature = body.temperature ?? 0.2;
    const maxTokens = body.maxTokens ?? 500;

    if (!code) {
      return new Response(
        JSON.stringify({ error: "Code is required" }),
        { status: 400 }
      );
    }

    const started = performance.now();

    // Build prompt for code completion
    const systemPrompt = `You are an expert code completion assistant. Complete the code provided by the user. Only return the completion, not the original code. Be concise and accurate. Language: ${language}`;

    // Call OpenRouter API
    const chatRes = await fetch(`${OPENROUTER_BASE_URL}/chat/completions`, {
      method: "POST",
      headers: {
        "Authorization": `Bearer ${OPENROUTER_API_KEY}`,
        "Content-Type": "application/json",
        "HTTP-Referer": SUPABASE_URL,
        "X-Title": "Code Completion Tool",
      },
      body: JSON.stringify({
        model,
        messages: [
          { role: "system", content: systemPrompt },
          { role: "user", content: `Complete this ${language} code:\n\n${code}` },
        ],
        temperature,
        max_tokens: maxTokens,
      }),
    });

    if (!chatRes.ok) {
      const errorText = await chatRes.text();
      console.error("OpenRouter error:", errorText);
      return new Response(
        JSON.stringify({ error: "Provider error", detail: errorText }),
        { status: 502 }
      );
    }

    const json: any = await chatRes.json();
    const completion = json?.choices?.[0]?.message?.content ?? "";

    if (!completion) {
      return new Response(
        JSON.stringify({ error: "Empty completion from provider" }),
        { status: 502 }
      );
    }

    const duration_ms = Math.round(performance.now() - started);
    const tokens = json?.usage
      ? {
          prompt: json.usage.prompt_tokens,
          completion: json.usage.completion_tokens,
          total: json.usage.total_tokens,
        }
      : undefined;

    const response: CodeCompletionResponse = {
      completion,
      model,
      duration_ms,
      tokens,
    };

    return new Response(JSON.stringify(response), {
      status: 200,
      headers: { "Content-Type": "application/json" },
    });
  } catch (e) {
    console.error("Unhandled error:", e);
    return new Response(
      JSON.stringify({ error: "Unhandled error", detail: String(e) }),
      { status: 500 }
    );
  }
});

